<?phpClass Controller{    protected static $params;    public $view_folder;    /**     * Controller constructor.     * @param $folder string     */    public function __construct($folder)    {        $this->view_folder = $folder;    }    /**     * Render method     * @param $view_file string     * @param array $data     */    public function render($view_file, $data = [])    {        $view = new View();        $full_path = $this->view_folder . DIRECTORY_SEPARATOR . $view_file;        $view->output($full_path, $data);    }    public static function setParams($params)    {        self::$params = $params;    }}